hudson.tasks.Shell.command:
# Get list of resources locked by this build. RESOURCES=${RESOURCES:-$(cd ${JENKINS_HOME} && ./resourceslockedby.py ${JOB_BASE_NAME}#${BUILD_NUMBER})}
# Kernel git repository to use: KERNELREPO=${KERNELREPO:-"https://github.com/torvalds/linux.git"}
# QEMU git repository to use: QEMUREPO=${QEMUREPO:-"https://github.com/qemu/qemu.git"}
# Kernel version to use. KERNELTAG=${KERNELTAG:-$(echo ${JOB_BASE_NAME} | grep -oP '(?<=\.)v.+\..+(?=\.)' || :;)}
# Qemu version to use. QEMUTAG=${QEMUTAG:-"v2.11.1"}
TIMEOUT=1800
# Run test on each resource. for R in ${RESOURCES}; do # In order to execute in parallel, each # ressource test run within its own subshell. ( # Insure resource provisioned for test. if ! (cd ${JENKINS_HOME} && ./checkresourceprovisioned.sh ${R}) &> ${R}.checkresourceprovisioned.log; then # Continue on to the next resource if # the provision-check fail on this resource. continue fi # Check if a new kernel need to be installed. if ssh -q jenkins@${R} "./checkfornewkerneltag.sh ${KERNELREPO}" ${KERNELTAG} &> ${R}.checkfornewkerneltag.log; then # Get the version built. kernbuilt=$(ssh -q jenkins@${R} ls kernel/linux-image* | grep -oP "(?<=linux-image-).+(?=-jenkins_)") # Reboot resource into kernel version built. (cd ${JENKINS_HOME} && ./rebootresource.sh jenkins@${R} ${kernbuilt} &> /dev/null) fi echo "${R}: $(ssh -q jenkins@${R} 'uname -r')" > ${R}.kernelrelease # Check if a new qemu need to be installed. ssh -q jenkins@${R} "./checkfornewqemutag.sh ${QEMUREPO}" ${QEMUTAG} &> ${R}.checkfornewqemutag.log || :; # Run test. timeout -sKILL ${TIMEOUT} ssh -q jenkins@${R} sudo ./avocado/contrib/testsuites/run-kvm-unit-test.sh &> ${R}.ssh.log || :; #if [ ${PIPESTATUS[0]} -eq 0 ]; then AVOCADO_LOG_PATH=$(cat ${R}.ssh.log | fgrep 'JOB LOG' | grep -oP '(?<=: ).+$') scp jenkins@${R}:${AVOCADO_LOG_PATH} ${R}.avocado.log #fi ) & done
# Wait for all subshells. wait
# Generate "Kernel Release:" line for # the "Set Build Description" plugin. echo -n "Kernel Release: " > kernelreleases for R in ${RESOURCES}; do if [ -f ${R}.kernelrelease ]; then echo -n "$(cat ${R}.kernelrelease 2>/dev/null | grep -oP '.+(?=-jenkins)'), " >> kernelreleases fi done truncate --size=-2 kernelreleases echo [$(cat kernelreleases)]
